import React, { Component } from 'react';
import axios from 'axios';

export default class Create extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      message: '',
    };
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    const inputValue = event.target.value;
    const stateField = event.target.name;
    this.setState({
      [stateField]: inputValue,
    });
    console.log(this.state);
  }
  async handleSubmit(event) {
    event.preventDefault();
    const { name, message } = this.state;
    await axios.post(
      'https://i149bstj8e.execute-api.us-east-1.amazonaws.com/default/serverlessAppFunction2',
      { create: `${name}, ${message}` },
    ).then((response)=>{
      if (response.status == "200") {
        this.setState({
          res: "Successfully deleted item: " + `${date}`,
          date: ""
        })
      } else {
        this.setState({
          res: "Error in deleting item: " + `${date}` + response.data.error
        })
      }
    });
  }

  render() {
    return (
        <form onSubmit={this.handleSubmit}>
        <div className="col col-lg-5">
            <input
                type="text"
                name="name"
                className="form-control"
                placeholder="Type your name"
                onChange={this.handleChange}
                value={this.state.name}
            />
        </div>
        <div className="col col-lg-5">
            <input
                type="text"
                name="message"
                className="form-control"
                placeholder="Type your message"
                onChange={this.handleChange}
                value={this.state.message}
            />
        </div>
        <div className="col col-lg-2">
            <button type="submit" className="btn btn-outline-secondary">Create</button>
        </div>
        </form>
    );
  }
}
