import React, { Component } from 'react';
import axios from 'axios';
import $ from "jquery";
import {fadeoutAlert} from '../customScript';

export default class Delete extends Component {
  constructor(props) {
    super(props);
    this.state = {
      res: ''
    };
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  async handleSubmit(event) {
    event.preventDefault();
    const { read } = this.state;
    await axios.get(
      'https://i149bstj8e.execute-api.us-east-1.amazonaws.com/default/serverlessAppFunction2',
    ).then((response)=>{
      if (response.status == "200") {
        this.setState({
          res: "Successfully fetched items: "
        })
      } else {
        this.setState({
          res: "Error in fetching item: " + response.data.error
        })
      }
    }).catch(function (error) {
      this.setState({
        res: "Error in fetching item: " + error
      })
    });
    fadeoutAlert();
  }

  render() {
    return (
        <>
        <form onSubmit={this.handleSubmit}>
        <div className="col col-lg-2">
            <button type="submit" className="btn btn-outline-secondary">Read</button>
        </div>
        </form>
        <br /><br /><div className="customAlert"><b>{this.state.res}</b></div>
        </>
    );
  }
}
