import React, { Component } from 'react';
import axios from 'axios';
import $ from "jquery";
import {fadeoutAlert} from '../customScript';

export default class Delete extends Component {
  constructor(props) {
    super(props);
    this.state = {
      date: '',
      res: ''
    };
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    const inputValue = event.target.value;
    const stateField = event.target.name;
    this.setState({
      [stateField]: inputValue,
    });
    console.log(this.state);
  }

  async handleSubmit(event) {
    event.preventDefault();
    const { date } = this.state;
    await axios.post(
      'https://i149bstj8e.execute-api.us-east-1.amazonaws.com/default/serverlessAppFunction2',
      { delete: `${date}` },
    ).then((response)=>{
      if (response.status == "200") {
        this.setState({
          res: "Successfully deleted item: " + `${date}`,
          date: ""
        })
      } else {
        this.setState({
          res: "Error in deleting item: " + `${date}` + response.data.error
        })
      }
    }).catch(function (error) {
      this.setState({
        res: "Error in deleting item: " + `${date}` + error
      })
    });
    fadeoutAlert();
  }

  render() {
    return (
        <>
        <form onSubmit={this.handleSubmit}>
        <div className="col col-lg-10">
            <input type="text" name="date" className="form-control" placeholder="Type unique date" onChange={this.handleChange} value={this.state.date} />
        </div>
        <div className="col col-lg-2">
            <button type="submit" className="btn btn-outline-secondary">Delete</button>
        </div>
        </form>
        <br /><br /><div className="customAlert"><b>{this.state.res}</b></div>
        </>
    );
  }
}
